-- Create "toolsets" table
CREATE TABLE "toolsets" ("id" uuid NOT NULL DEFAULT generate_uuidv7(), "organization_id" uuid NOT NULL, "project_id" uuid NOT NULL, "name" text NOT NULL, "description" text NULL, "http_tool_ids" uuid[] NULL, "created_at" timestamptz NOT NULL DEFAULT clock_timestamp(), "updated_at" timestamptz NOT NULL DEFAULT clock_timestamp(), "deleted_at" timestamptz NULL, "deleted" boolean NOT NULL GENERATED ALWAYS AS (deleted_at IS NOT NULL) STORED, PRIMARY KEY ("id"), CONSTRAINT "toolsets_project_id_name_key" UNIQUE ("project_id", "name"), CONSTRAINT "toolsets_project_id_fkey" FOREIGN KEY ("project_id") REFERENCES "projects" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
