-- Create "slack_app_connections" table
CREATE TABLE "slack_app_connections" ("organization_id" text NOT NULL, "project_id" uuid NOT NULL, "access_token" text NOT NULL, "slack_team_name" text NOT NULL, "slack_team_id" text NOT NULL, "default_toolset_slug" text NULL, "created_at" timestamptz NOT NULL DEFAULT clock_timestamp(), "updated_at" timestamptz NOT NULL DEFAULT clock_timestamp(), CONSTRAINT "slack_auth_connections_organization_id_project_id_key" UNIQUE ("organization_id", "project_id"), CONSTRAINT "slack_auth_connections_slack_team_id_key" UNIQUE ("slack_team_id"), CONSTRAINT "slack_auth_connections_project_id_fkey" FOREIGN KEY ("project_id") REFERENCES "projects" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "slack_app_connections_default_toolset_slug_check" CHECK ((default_toolset_slug IS NULL) OR ((default_toolset_slug <> ''::text) AND (char_length(default_toolset_slug) <= 40))));
