-- Modify "environments" table
ALTER TABLE "environments" DROP CONSTRAINT "environments_description_check", ADD CONSTRAINT "environments_description_check" CHECK ((description <> ''::text) AND (char_length(description) <= 250)), DROP CONSTRAINT "environments_name_check", ADD CONSTRAINT "environments_name_check" CHECK ((name <> ''::text) AND (char_length(name) <= 60)), DROP CONSTRAINT "environments_slug_check", ADD CONSTRAINT "environments_slug_check" CHECK ((slug <> ''::text) AND (char_length(slug) <= 60));
-- Modify "prompt_templates" table
ALTER TABLE "prompt_templates" DROP CONSTRAINT "prompt_templates_name_check", ADD CONSTRAINT "prompt_templates_name_check" CHECK ((name <> ''::text) AND (char_length(name) <= 60));
