-- Modify "toolsets" table
ALTER TABLE "toolsets" DROP CONSTRAINT "toolsets_default_environment_slug_check", ADD CONSTRAINT "toolsets_default_environment_slug_check" CHECK ((default_environment_slug <> ''::text) AND (char_length(default_environment_slug) <= 60)), DROP CONSTRAINT "toolsets_description_check", ADD CONSTRAINT "toolsets_description_check" CHECK ((description <> ''::text) AND (char_length(description) <= 250)), DROP CONSTRAINT "toolsets_http_tool_names_check", ADD CONSTRAINT "toolsets_http_tool_names_check" CHECK (array_length(http_tool_names, 1) <= 5000), DROP CONSTRAINT "toolsets_mcp_slug_check", ADD CONSTRAINT "toolsets_mcp_slug_check" CHECK ((mcp_slug IS NULL) OR ((mcp_slug <> ''::text) AND (char_length(mcp_slug) <= 60))), DROP CONSTRAINT "toolsets_name_check", ADD CONSTRAINT "toolsets_name_check" CHECK ((name <> ''::text) AND (char_length(name) <= 60)), DROP CONSTRAINT "toolsets_slug_check", ADD CONSTRAINT "toolsets_slug_check" CHECK ((slug <> ''::text) AND (char_length(slug) <= 60));
